import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom"
import api from "../../services/services";
import { Container } from "./SignUpStyle";

export default function Home() {
    const [newUser, setNewUser] = useState(
        {
            CPForCNPJ: '',
            fantasyName: '',
            businessName: '',
            adress: '',
            city: '',
            contact: '',
            telephone: '',
            email: '',
            neighborhood: '',
            uf: '',
            cep: ''
        }
    )

    function handleSubmit(e: any) {
        e.preventDefault();

        fetch("http://localhost:3201/sistema/usuarios", {
            method: "post",
            body: JSON.stringify(newUser),
            headers: {
                "Content-Type": "application/json"
            }
        })
            .then(response => response.json())
            .then(data => {

                // faÃ§a algo com a resposta da API
            })
            .catch(error => {
                console.error("Erro ao enviar os dados para a API:", error);
            });
    }

    function handleInputChange(e: any) {
        const { name, value } = e;

        setNewUser(prevState => {
            return { ...prevState, [name]: value }
        });

    }

    return (
        <div>
            <Container className="container">
                <form action="" onSubmit={(e) => handleSubmit(e)}>
                    <div className="row">
                        <div className="user-insert">
                            <label htmlFor="CPForCNPJ">CPF ou CNPJ</label>
                            <input
                                type="text"
                                id="CPForCNPJ"
                                name="CPForCNPJ"
                                placeholder="Insira o seu nome"
                                minLength={11}
                                maxLength={14}
                                required
                                value={newUser.CPForCNPJ}
                                onChange={(e) => handleInputChange(e.target)}
                            />
                        </div>
                        <div className="user-insert">
                            <label htmlFor="contact">Contato</label>
                            <input
                                type="text"
                                id="contact"
                                name="contact"
                                placeholder="Insira o seu nome"
                                minLength={3}
                                maxLength={100}
                                required
                                value={newUser.contact}
                                onChange={(e) => handleInputChange(e.target)}
                            />
                        </div>
                    </div>
                    <div className="row">
                        <div className="user-insert">
                            <label htmlFor="fantasyName">Nome Fantasia</label>
                            <input
                                type="text"
                                id="fantasyName"
                                name="fantasyName"
                                placeholder="Insira o seu nome"
                                minLength={3}
                                maxLength={100}
                                required
                                value={newUser.fantasyName}
                                onChange={(e) => handleInputChange(e.target)}
                            />
                        </div>
                        <div className="user-insert">
                            <label htmlFor="telephone">Telefone do Contato</label>
                            <input
                                type="text"
                                id="telephone"
                                name="telephone"
                                placeholder="Insira o seu nome"
                                minLength={11}
                                maxLength={11}
                                required
                                value={newUser.telephone}
                                onChange={(e) => handleInputChange(e.target)}
                            />
                        </div>
                    </div>
                    <div className="row">
                        <div className="user-insert">
                            <label htmlFor="businessName">RazÃ£o Social</label>
                            <input
                                type="text"
                                id="businessName"
                                name="businessName"
                                placeholder="Insira o seu nome"
                                minLength={3}
                                maxLength={100}
                                required
                                value={newUser.businessName}
                                onChange={(e) => handleInputChange(e.target)}
                            />
                        </div>
                        <div className="user-insert">
                            <label htmlFor="email">E-mail de Contato</label>
                            <input
                                type="text"
                                id="email"
                                name="email"
                                placeholder="Insira o seu nome"
                                minLength={3}
                                maxLength={100}
                                required
                                value={newUser.email}
                                onChange={(e) => handleInputChange(e.target)}
                            />
                        </div>
                    </div>
                    <div className="row">
                        <div className="user-insert">
                            <label htmlFor="adress">EndereÃ§o</label>
                            <input
                                type="text"
                                id="adress"
                                name="adress"
                                placeholder="Insira o seu nome"
                                minLength={3}
                                maxLength={100}
                                required
                                value={newUser.adress}
                                onChange={(e) => handleInputChange(e.target)}
                            />
                        </div>
                        <div className="user-insert">
                            <label htmlFor="neighborhood">Bairro</label>
                            <input
                                type="text"
                                id="neighborhood"
                                name="neighborhood"
                                placeholder="Insira o seu nome"
                                minLength={3}
                                maxLength={100}
                                required
                                value={newUser.neighborhood}
                                onChange={(e) => handleInputChange(e.target)}
                            />
                        </div>
                    </div>
                    <div className="row">
                        <div className="user-insert">
                            <label htmlFor="city">Cidade</label>
                            <input
                                type="text"
                                id="city"
                                name="city"
                                placeholder="Insira o seu nome"
                                minLength={3}
                                maxLength={100}
                                required
                                value={newUser.city}
                                onChange={(e) => handleInputChange(e.target)}
                            />
                        </div>
                        <div className="user-insert">
                            <label htmlFor="uf">UF</label>
                            <input
                                type="text"
                                id="uf"
                                name="uf"
                                placeholder="Insira o seu nome"
                                minLength={3}
                                maxLength={100}
                                required
                                value={newUser.uf}
                                onChange={(e) => handleInputChange(e.target)}
                            />
                        </div>
                        <div className="user-insert">
                            <label htmlFor="">CEP</label>
                            <input type="text" />
                        </div>
                    </div>
                    <button>Cancelar</button>
                    <button>Salvar</button>
                </form>
            </Container>
        </div>
    )
}
