import React, { useState, useEffect } from "react";
import api from '../../services/services';
import { Link } from "react-router-dom"
import { Container } from "./HomeStyle";
import Button from "../../components/Button";
import Delete from "../../assets/delete.png";
import Edit from "../../assets/edit.png";

export default function Home() {
    const [usuarios, setUsuarios] = useState([]);
    const [page, setPage] = useState(1);
    const [apiInfo, setApiInfo] = useState(undefined);

    const [showInfo, setShowInfo] = useState(false);

    useEffect(() => {
        loadUsuarios();
    }, [])

    useEffect(() => {
        if (usuarios !== undefined) {
            setShowInfo(true);
        }
    }, [usuarios]);

    async function loadUsuarios() {
        const response = await api.get(`/persons`);

        const { docs, ...usuariosInfo } = response.data;

        setApiInfo(usuariosInfo);
        setUsuarios(response.data);
    }

    function deleteUser(id: String) {
        fetch(`http://localhost:3201/sistema/persons/${id}`, {
            method: "delete",
            headers: {
                "Content-Type": "application/json"
            }
        })
            .catch(error => {
                console.error("Erro ao enviar os dados para a API:", error);
            });


        loadUsuarios();
    }

    function filter() {
        const $code = document.getElementById('code') as HTMLInputElement;
        const $fantasyName = document.getElementById('fantasyName') as HTMLInputElement;
        const $businessName = document.getElementById('businessName') as HTMLInputElement;

        if ($code.value === "" && $fantasyName.value === "" && $businessName.value === "") return;

        let filterUser = usuarios.filter((item: any) => {
            return (item._id === $code.value || item.fantasyName === $fantasyName.value.toUpperCase() || item.businessName === $businessName.value.toUpperCase())
        });
        setUsuarios(filterUser);
    }

    return (
        <div>
            {
                !showInfo
                    ?
                    <div></div>
                    :
                    <Container className="container">
                        <h1>Listagem de pessoas</h1>
                        <fieldset>
                            <div className="inputs-container">
                                <div className="col col-lg-3 user-insert">
                                    <label htmlFor="code">Código</label>
                                    <input
                                        type="text"
                                        id="code"
                                        name="code"
                                    />
                                </div>
                                <div className="col col-lg-8 user-insert">
                                    <label htmlFor="fantasyName">Nome Fantasia</label>
                                    <input
                                        type="text"
                                        id="fantasyName"
                                        name="fantasyName"
                                    />
                                </div>
                                <div className="col col-lg-8 user-insert">
                                    <label htmlFor="businessName">Razão Social</label>
                                    <input
                                        type="text"
                                        id="businessName"
                                        name="businessName"
                                    />
                                </div>
                                <Button typeHTML="" onClick={filter} text="Filtrar" className="col col-lg-2" />
                            </div>
                        </fieldset>
                        <div className="row include-btn-container">
                            <Link to="/create">
                                <Button typeHTML="" text="Incluir" onClick="" className="col col-lg-1"/>
                            </Link>
                        </div>
                        <div className="table-responsive">
                            <table className="table">
                                <thead>
                                    <tr>
                                        <th scope="col">Código</th>
                                        <th scope="col">Nome</th>
                                        <th scope="col">Cidade</th>
                                        <th scope="col">UF</th>
                                        <th scope="col">Ações</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {usuarios.map((usuario: any) => (
                                        <tr key={usuario._id}>
                                            <th scope="row">0001</th>
                                            <td>{usuario.fantasyName}</td>
                                            <td>{usuario.city}</td>
                                            <td>{usuario.uf}</td>
                                            <td>
                                                <Link to={`/update/${usuario._id}`}>
                                                    <img src="" />
                                                </Link>
                                                <button>Editar</button>
                                                <button onClick={() => deleteUser(usuario._id)}>Excluir</button>
                                            </td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        </div>
                    </Container>
            }
        </div>
    )
}
